-- BLOCK 1: Create database (run, then switch 'Connect to' dropdown to fraud_demo) 

CREATE DATABASE fraud_demo; 



-- BLOCK 2: Master key and credentials 

CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'Demo@Password123!'; 



CREATE DATABASE SCOPED CREDENTIAL adls_cred 

    WITH IDENTITY = 'Managed Identity'; 



CREATE EXTERNAL DATA SOURCE adls_raw 

    WITH ( LOCATION = 'abfss://synapse@adlsfrauddemo.dfs.core.windows.net/', 

           CREDENTIAL = adls_cred ); 



CREATE EXTERNAL FILE FORMAT csv_fmt 

    WITH ( FORMAT_TYPE = DELIMITEDTEXT, 

           FORMAT_OPTIONS (FIELD_TERMINATOR = ',', FIRST_ROW = 2) ); 



-- BLOCK 3: External table for transactions 

CREATE EXTERNAL TABLE dbo.transactions ( 

    transaction_id     VARCHAR(20), customer_id      VARCHAR(15), 

    account_id         VARCHAR(15), transaction_date VARCHAR(30), 

    amount_usd         FLOAT,       merchant_category VARCHAR(30), 

    channel            VARCHAR(20), country_code     VARCHAR(5), 

    is_fraud_label     INT,         fraud_score      FLOAT, 

    ip_address         VARCHAR(20), device_fingerprint VARCHAR(50) 

) 

WITH ( LOCATION = 'raw/transactions/', 

       DATA_SOURCE = adls_raw, 

       FILE_FORMAT = csv_fmt ); 



-- BLOCK 4: Verify 

SELECT TOP 10 * FROM dbo.transactions;
